import { Input, Button, InputGroup } from 'reactstrap';
import React from 'react';
import { FaExchangeAlt } from 'react-icons/fa';

const Inputs = props => (
    <div>
        <InputGroup>
            <Input type="number" 
                onChange={props.handleChange} 
                value={props.weight}
                placeholder='Enter 1 rep max' />                 
                <Button color='info' 
                        size="sm" 
                        onClick={props.isKilos}>
                {!props.tMass? 'kg ' : 'lb '}{<FaExchangeAlt />}{props.tMass? ' kg' : ' lb'}
                </Button>          
        </InputGroup>
        
        <br />

        <InputGroup>
            <Input type="number" 
                onChange={props.lessBar} 
                value={props.bar} 
                placeholder='Enter bar weight (B)' />             
            <Button color='info' 
                    size="sm" 
                    onClick={props.isBarKilos}>
                {!props.bMass? 'kg ' : 'lb '}{<FaExchangeAlt />}{props.bMass? ' kg' : ' lb'}
            </Button>
        </InputGroup>
            
    </div>
)
    
export default Inputs;
