import React, { Component } from 'react';
import { Button } from 'reactstrap';

class Bench extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: [],
            isToggle: false,
            isKilos: false,
        }

        this.handleChange = this.handleChange.bind(this);
        this.warmupClick = this.warmupClick.bind(this);
        this.isKilos = this.isKilos.bind(this);
    }

    handleChange(e) {
        e.preventDefault();
        this.setState({
            value: e.target.value,
        })
    }   

    warmupClick(e) {
        this.setState({
            isToggle: !this.state.isToggle,
        })
    }

    isKilos() {
       this.setState({
           isKilos: !this.state.isKilos,
       })
    }

    render() {
        let weight = this.state.value;
        let rep1, rep2, rep3;
        let pc1, pc2, pc3;
        let cycle;
        let mass = this.state.isKilos;


        return (
            <div>
                Bench: 
                <span>
                    <input onChange={this.handleChange}></input>
                    <Button onClick={this.isKilos}>{mass ? 'Kg' : 'Lb'}</Button>
                </span>
                <div>
                    <Button className="btn btn-primary"
                            onClick={this.warmupClick}>
                            Warm up
                    </Button>
                    <div className='container'
                         style={{display: this.state.isToggle ? 'block' : 'none'}} 
                         >
                        <div>40% Set 1 x 5 : {weight*4/10}{mass}</div>
                        <div>50% Set 2 x 5 : {weight*5/10}{mass}</div>
                        <div>60% Set 3 x 3 : {weight*6/10}{mass}</div>
                    </div>
                </div>

                <div>{pc1}%: {rep1}x {weight*pc1/100}{mass}</div>
                <div>{pc2}%: {rep2}x {weight*pc2/100}{mass}</div>
                <div>{pc3}%: {rep3}x+ {weight*pc3/100}{mass}</div>

            </div>
        )
    }
}

export default Bench
