import React, { Component } from 'react';
import { Button } from 'reactstrap';
import CycleA from './CycleA';
import CycleB from './CycleB';
import CycleC from './CycleC';
import Deload from './Deload';
import bench from './bench.css';

class Bench extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: [0],
            bar: [20],
            isToggle: false,
            isKilos: false,
            isBarKilos: false,
            isDeload: false,
            isCycleA: true,
            isCycleB: false,
            isCycleC: false,
        }

        this.handleChange = this.handleChange.bind(this);
        this.warmupClick = this.warmupClick.bind(this);
        this.isKilos = this.isKilos.bind(this);
        this.isBarKilos = this.isBarKilos.bind(this);
        this.lessBar = this.lessBar.bind(this);
        this.cycleA = this.cycleA.bind(this);
        this.cycleB = this.cycleB.bind(this);
        this.cycleC = this.cycleC.bind(this);
        this.deload = this.deload.bind(this);
    }

    handleChange(e) {
        e.preventDefault();
        this.setState({
            value: e.target.value,
        })
    }   

    lessBar(f) {
        f.preventDefault();
        this.setState({
            bar: f.target.value,
        })
    }   

    warmupClick(e) {
        this.setState({
            isToggle: !this.state.isToggle,
        })
    }

    isKilos(e) {
       this.setState({
           isKilos: !this.state.isKilos,
       })

       if(this.state.isKilos === false) {
            this.setState({
                value: Math.round(this.state.value / 0.45359237).toFixed(1),
            })
       } else {
        this.setState({
            value: Math.round(this.state.value * 0.45359237).toFixed(1),
        })
       }
    }


    isBarKilos(e) {
        this.setState({
            isBarKilos: !this.state.isBarKilos,
        })
 
        if(this.state.isBarKilos === false) {
             this.setState({
                 bar: Math.round(this.state.bar / 0.45359237).toFixed(1),
             })
        } else {
         this.setState({
             bar: Math.round(this.state.bar * 0.45359237).toFixed(1),
         })
        }
     }

     cycleA() {
         this.setState({
             isCycleA: true,
             isCycleB: false,
             isCycleC: false,
             isDeload: false,
         })
     }

     cycleB() {
        this.setState({
            isCycleA: false,
            isCycleB: true,
            isCycleC: false,
            isDeload: false,
        })
    }

    cycleC() {
        this.setState({
            isCycleA: false,
            isCycleB: false,
            isCycleC: true,
            isDeload: false,
        })
    }

    deload() {
        this.setState({
            isCycleA: false,
            isCycleB: false,
            isCycleC: false,
            isDeload: true,
        })
    }

    render() {
        let bar = this.state.bar;
        let weight = this.state.value;
        let mass = !this.state.isKilos ? 'Kg' : 'Lb';
        let barMass = !this.state.isBarKilos ? 'Kg' : 'Lb';

            return (
                <div>
                    <div>
                    Bench: 
                        <span>
                            <input type="number" min='0' onChange={this.handleChange} value={weight}></input>
                            <Button onClick={this.isKilos}>{mass}</Button>
                        </span>
                    </div>
                    <div>
                    Bar:
                        <span>
                            <input type="number" min='0' onChange={this.lessBar} value={bar}></input>
                            <Button onClick={this.isBarKilos}>{barMass}</Button>
                        </span>
                    </div>
                    <div>
                        <Button color="blue"
                                className="btn btn-primary"
                                onClick={this.warmupClick}>
                                Warm up
                        </Button>
                        <Button className="btn btn-primary"
                                onClick={this.cycleA}>
                                Cycle A
                        </Button>
                        <Button className="btn btn-primary"
                                onClick={this.cycleB}>
                                Cycle B
                        </Button>
                        <Button className="btn btn-primary"
                                onClick={this.cycleC}>
                                Cycle C
                        </Button>

                        <Button className="btn btn-primary"
                                onClick={this.deload}>
                                Deload
                        </Button>
                    </div>
                    <div style={{ display: this.state.isCycleA ? 'block' : 'none' }}>
                        <CycleA weight={weight} bar={bar} isToggle={this.state.isToggle}/>
                    </div>
                    <div style={{ display: this.state.isCycleB ? 'block' : 'none' }}>
                        <CycleB weight={weight} bar={bar} isToggle={this.state.isToggle}/>
                    </div>
                    <div style={{ display: this.state.isCycleC ? 'block' : 'none' }}>
                        <CycleC weight={weight} bar={bar} isToggle={this.state.isToggle}/>
                    </div>
                    <div style={{ display: this.state.isDeload ? 'block' : 'none' }}>
                        <Deload weight={weight} bar={bar}/>
                    </div>
                </div>
            )
    }   
}

export default Bench
