import React, { Component } from 'react';
import { Button } from 'reactstrap';

class Bench extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: [],
            isToggle: false,
        }

        this.handleChange = this.handleChange.bind(this);
        this.warmupClick = this.warmupClick.bind(this);
    }

    handleChange(e) {
        e.preventDefault();
        this.setState({
            value: e.target.value,
        })
    }   

    warmupClick(e) {
        this.setState({
            isToggle: !this.state.isToggle,
        })
    }

    render() {
        let weight = this.state.value;
        let mass = "kg";
        
        return (
            <div>
                Bench: 
                <span>
                    <input onChange={this.handleChange}></input>
                </span>
                <div>
                    <Button className="btn btn-primary"
                            onClick={this.warmupClick}>
                            Warm up
                    </Button>
                    <div className='container'
                         style={{display: this.state.isToggle ? 'block' : 'none'}} 
                         >
                        <div>Warm up x5 40% of max: {weight*4/10}{mass}</div>
                        <div>Warm up x5 50% of max: {weight*5/10}{mass}</div>
                        <div>Warm up x3 60% of max: {weight*6/10}{mass}</div>
                    </div>
                </div>

                <div>70%: {rep1}x {weight*7/10}{mass}</div>
                <div>80%: {rep2}x {weight*8/10}{mass}</div>
                <div>90%: {rep3}x {weight*9/10}{mass}</div>

            </div>
        )
    }
}

export default Bench
