import React, { Component } from 'react';
import CycleA from './CycleA';
import CycleB from './CycleB';
import CycleC from './CycleC';
import Deload from './Deload';
import CycleButtons from './CycleButtons';
import Inputs from './Inputs';
import '../App.css';


class Bench extends Component {
    state = {
        value: [0],
        bar: [0],
        isToggle: false,
        isKilos: false,
        isBarKilos: false,
        isDeload: false,
        isCycleA: true,
        isCycleB: false,
        isCycleC: false,
        rSelected: 1,
        warmupTotal: 0,
        cycleATotal: 0,
        cycleBTotal: 0,
        cycleCTotal: 0,
    }
    
    onRadioBtnClick = rSelected => {
        this.setState({ rSelected });
      }

    handleChange = e => {
        e.preventDefault();
        this.setState({
            value: e.target.value,
            warmupTotal: e.target.value*4/10*5+e.target.value*5/10*5+e.target.value*6/10*3,
            cycleATotal: e.target.value*65/100*5+e.target.value*75/100*5+e.target.value*85/100*5,
            cycleBTotal: e.target.value*70/100*3+e.target.value*80/100*3+e.target.value*90/100*3,
            cycleCTotal: e.target.value*75/100*5+e.target.value*85/100*3+e.target.value*95/100*1,
        })
    }   

    lessBar = e => {
        e.preventDefault();
        this.setState({
            bar: e.target.value,
        })
    }   

    warmupToggle = () => {
        this.setState({
            isToggle: !this.state.isToggle,
        })
    }

    isKilos = () => {
       this.setState({
           isKilos: !this.state.isKilos,
       })

       if(this.state.isKilos === false) {
            this.setState({
                value: this.state.value / 0.45359237,
                warmupTotal: (this.state.value*40/100*5+this.state.value*50/100*5+this.state.value*60/100*3) / 0.45359237,
                cycleATotal: (this.state.value*65/100*5+this.state.value*75/100*5+this.state.value*85/100*5) / 0.45359237,
                cycleBTotal: (this.state.value*70/100*3+this.state.value*80/100*3+this.state.value*90/100*3) / 0.45359237,
                cycleCTotal: (this.state.value*75/100*5+this.state.value*85/100*3+this.state.value*95/100*1) / 0.45359237,
            })
       } else {
        this.setState({
            value: this.state.value * 0.45359237,
            warmupTotal: (this.state.value*40/100*5+this.state.value*50/100*5+this.state.value*60/100*3) * 0.45359237,
            cycleATotal: (this.state.value*65/100*5+this.state.value*75/100*5+this.state.value*85/100*5) * 0.45359237,
            cycleBTotal: (this.state.value*70/100*3+this.state.value*80/100*3+this.state.value*90/100*3) * 0.45359237,
            cycleCTotal: (this.state.value*75/100*5+this.state.value*85/100*3+this.state.value*95/100*1) * 0.45359237,
    })
       }
    }

    isBarKilos = () => {
        this.setState({
            isBarKilos: !this.state.isBarKilos,
        })
 
        if(this.state.isBarKilos === false) {
             this.setState({
                 bar: this.state.bar / 0.45359237,
             })
        } else {
         this.setState({
             bar: this.state.bar * 0.45359237,
         })
        }
     }

     cycleA = () => {
         this.setState({
             isCycleA: true,
             isCycleB: false,
             isCycleC: false,
             isDeload: false,
             rSelected: 1,
         })
     }

     cycleB = () => {
        this.setState({
            isCycleA: false,
            isCycleB: true,
            isCycleC: false,
            isDeload: false,
            rSelected: 2,
        })
    }

    cycleC = () => {
        this.setState({
            isCycleA: false,
            isCycleB: false,
            isCycleC: true,
            isDeload: false,
            rSelected: 3,
        })
    }

    deload = () => {
        this.setState({
            isCycleA: false,
            isCycleB: false,
            isCycleC: false,
            isDeload: true,
            rSelected: 0,
        })
    }

    
    render() {
        const { bar, rSelected, isCycleA, isCycleB, isCycleC, cycleATotal, cycleBTotal, cycleCTotal, warmupTotal, isKilos, isBarKilos, isDeload, isToggle } = this.state;
        let weight = this.state.value;
        function round(num) {
            return Math.round(num*2)/2;
        }

            return (
                <div className="body">
                    <div className="liftHeader">BENCH</div> 
                    <Inputs className="Inputs"
                            handleChange={this.handleChange}
                            lessBar={this.lessBar}
                            isBarKilos={this.isBarKilos}
                            isKilos={this.isKilos}
                            bar={bar}
                            weight={weight}
                            tMass={isKilos}
                            bMass={isBarKilos}
                            round={round}
                    />
                    <div className="Cycle-Buttons">
                    <CycleButtons warmupToggle={this.warmupToggle} 
                                  deload={this.deload} 
                                  cycleA={this.cycleA} 
                                  cycleB={this.cycleB} 
                                  cycleC={this.cycleC}
                                  rSelected={rSelected}
                    />  
                    </div>    
                    <div>
                        <div style={{ display: isCycleA ? 'inline' : 'none' }}>
                            <CycleA weight={weight} 
                                    bar={bar} 
                                    isToggle={isToggle} 
                                    round={round}
                                    cycleATotal={cycleATotal}
                                    warmupTotal={warmupTotal}
                                    tMass={isKilos}
                                    bMass={isBarKilos}
                                    />
                        </div>
                        <div style={{ display: isCycleB ? 'inline' : 'none' }}>
                            <CycleB weight={weight} 
                                    bar={bar} 
                                    isToggle={isToggle} 
                                    round={round}
                                    cycleBTotal={cycleBTotal}
                                    warmupTotal={warmupTotal}
                                    tMass={isKilos}
                                    bMass={isBarKilos}
                                    />
                        </div>
                        <div style={{ display: isCycleC ? 'inline' : 'none' }}>
                            <CycleC weight={weight} 
                                    bar={bar} 
                                    isToggle={isToggle} 
                                    round={round}
                                    cycleCTotal={cycleCTotal}
                                    warmupTotal={warmupTotal}
                                    tMass={isKilos}
                                    bMass={isBarKilos}
                                    />
                        </div>
                        <div style={{ display: isDeload ? 'inline' : 'none' }}>
                            <Deload weight={weight} 
                                    bar={bar}
                                    round={round}
                                    tMass={isKilos}
                                    bMass={isBarKilos}
                                    />
                        </div>
                    </div>
                </div>                
            )
    }   
}

export default Bench
