import React, { Component } from 'react';
import Bench from './Components/bench';
import Deadlift from './Components/deadlift';
import Squat from './Components/squat';
import OHPress from './Components/ohpress';
import Other from './Components/other';
import Tabs from './Components/Tabs';
import './App.css'
import NaviBar from './Components/NaviBar';
import { TabPane, TabContent } from 'reactstrap';
import CycleButtons from './Components/CycleButtons';

class App extends Component {
  state = { 
    activeTab: '1',
    isDeload: false,
    isCycleA: true,
    isCycleB: false,
    isCycleC: false,
    rSelected: 1
  };

  toggle = tab => {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  onRadioBtnClick = rSelected => {
    this.setState({ rSelected });
  }

  cycleA = () => {
    this.setState({
        isCycleA: true,
        isCycleB: false,
        isCycleC: false,
        isDeload: false,
        rSelected: 1,
    })
}

cycleB = () => {
   this.setState({
       isCycleA: false,
       isCycleB: true,
       isCycleC: false,
       isDeload: false,
       rSelected: 2,
   })
}

cycleC = () => {
   this.setState({
       isCycleA: false,
       isCycleB: false,
       isCycleC: true,
       isDeload: false,
       rSelected: 3,
   })
}

deload = () => {
   this.setState({
       isCycleA: false,
       isCycleB: false,
       isCycleC: false,
       isDeload: true,
       rSelected: 0,
   })
}

  render() {
    const { activeTab, deload, cycleA, cycleB, cycleC, rSelected } = this.state;
    return (
      <div>
        <NaviBar />
        <Tabs toggle={this.toggle}
              activeTab={activeTab}/>
        <div className="App">
          <div className='mainbody'>
            <br />
            <h4>Based on <a href='https://jimwendler.com/'>Wendler's 531 strength program</a></h4>
            <ul> 
              <li>Each week is a different cycle</li>
              <li>Maintain 1rm for all cycles</li>
              <li>Take a deload/rest week to recover</li>
              <li>Increase 1rm by 2.5/5kg and start new cycles</li>
              <li>Divide W-B by 2 to determine how much weight to put on each side of bar</li>
            </ul>
            <CycleButtons warmupToggle={this.warmupToggle} 
                                  deload={deload} 
                                  cycleA={cycleA} 
                                  cycleB={cycleB} 
                                  cycleC={cycleC}
                                  rSelected={rSelected}
                    />  
            <TabContent activeTab={activeTab}>
              <TabPane tabId='1'>
                <Bench cycleA={this.cycleA}
                       cycleB={this.cycleB}
                       cycleC={this.cycleC}
                       deload={this.deload}
                       rSelected={this.onRadioBtnClick}
                      />
              </TabPane>

              <TabPane tabId='2'>
                <Deadlift />
              </TabPane>

              <TabPane tabId='3'>
                <Squat />
              </TabPane>

              <TabPane tabId='4'>
                <OHPress />
              </TabPane>

              <TabPane tabId='5'>
                <Other />
              </TabPane>

            </TabContent>
          </div>
        </div>
      </div>

    );
  } 
}

export default App;
