import React, { Component } from 'react';
import { Button } from 'reactstrap';

class Bench extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: [],
            bar: [],
            isToggle: false,
            isKilos: false,
            setA: [],
            setB: [],
            setC: [],
            rep1: [],
            rep2: [],
            rep3: [],
        }

        this.handleChange = this.handleChange.bind(this);
        this.warmupClick = this.warmupClick.bind(this);
        this.isKilos = this.isKilos.bind(this);
        this.cycleA = this.cycleA.bind(this);
        this.cycleB = this.cycleB.bind(this);
        this.cycleC = this.cycleC.bind(this);

    }

    handleChange(e) {
        e.preventDefault();
        this.setState({
            value: e.target.value,
        })
    }   

    warmupClick(e) {
        this.setState({
            isToggle: !this.state.isToggle,
        })
    }

    isKilos(e) {
       this.setState({
           isKilos: !this.state.isKilos,
       })

       if(this.state.isKilos === false) {
            this.setState({
                value: Math.round(this.state.value / 0.45359237),
            })
       } else {
        this.setState({
            value: Math.round(this.state.value * 0.45359237),
        })
       }
    }

    cycleA() {
        this.setState({
            pc1: 65,
            pc2: 75,
            pc3: 85,
            rep1: 5,
            rep2: 5,
            rep3: 5,
        })
    }

    cycleB() {
        this.setState({
            pc1: 70,
            pc2: 80,
            pc3: 90,
            rep1: 3,
            rep2: 3,
            rep3: 3,
        })
    }

    cycleC() {
        this.setState({
            pc1: 75,
            pc2: 85,
            pc3: 95,
            rep1: 5,
            rep2: 3,
            rep3: 1,
        })
    }

    render() {
        let weight = this.state.value;
        let mass = !this.state.isKilos ? 'Kg' : 'Lb';
        let pc1 = this.state.pc1;
        let pc2 = this.state.pc2;
        let pc3 = this.state.pc3;
        let rep1 = this.state.rep1;
        let rep2 = this.state.rep2;
        let rep3 = this.state.rep3;


        return (
            <div>
                Bench: 
                <span>
                    <input onChange={this.handleChange} value={weight}></input>
                    <Button onClick={this.isKilos}>{mass}</Button>
                </span>
                <div>
                    <Button className="btn btn-primary"
                            onClick={this.warmupClick}>
                            Warm up
                    </Button>
                    <Button className="btn btn-primary"
                            onClick={this.cycleA}>
                            Cycle A
                    </Button>
                    <Button className="btn btn-primary"
                            onClick={this.cycleB}>
                            Cycle B
                    </Button>
                    <Button className="btn btn-primary"
                            onClick={this.cycleC}>
                            Cycle C
                    </Button>

                    <div className='container'
                         style={{display: this.state.isToggle ? 'block' : 'none'}} 
                         >
                        <div>40% Set 1 x 5 : {weight*4/10}{mass}</div>
                        <div>50% Set 2 x 5 : {weight*5/10}{mass}</div>
                        <div>60% Set 3 x 3 : {weight*6/10}{mass}</div>
                    </div>
                </div>

                <div>{pc1}%: {rep1}x {weight*pc1/100}{mass}</div>
                <div>{pc2}%: {rep2}x {weight*pc2/100}{mass}</div>
                <div>{pc3}%: {rep3}x+ {weight*pc3/100}{mass}</div>

            </div>
        )
    }
}

export default Bench
