import { Input, InputGroup, Button } from 'reactstrap';
import React, { Component } from 'react';

class Inputs extends Component {
    render() {
        return (
            <InputGroup>
                <Input type="number" min='0' onChange={this.props.handleChange} value={this.props.weight}></Input>
                <Button onClick={this.props.isKilos}>kg</Button>
                <Input type="number" min='0' onChange={this.props.lessBar} value={this.props.bar}></Input>
                <Button onClick={this.props.isBarKilos}>kg</Button>
            </InputGroup>
        )
    }
}

export default Inputs;
